              ST="SELECT count(PASSENGER_NAME) FROM railway_database where TRAIN_NAME='{0}' AND PHONE_NUMBER='{1}' AND PASSENGER_DATE_OF_BIRTH='{2}' AND DATE_OF_TRAVEL='{3}' ".format(train_name,phone_number,date_of_birth,date_of_travel)
              cursor.execute(ST) 
              mycon.close()
              data1=cursor.fetchone()
