                     ST="delete from railway_database where TRAIN_NAME='{0}' AND PHONE_NUMBER='{1}' AND PASSENGER_DATE_OF_BIRTH='{2}' AND DATE_OF_TRAVEL='{3}' AND PASSENGER_NAME='{4}' ".format(train_name,phone_number,date_of_birth,date_of_travel,passenger_name)
                     cursor.execute(ST) 
